//Organiza la informacion para crear las tablas de estadisticas
function crearTablaEstadisticas(ejer1, ejer2, contenedorId) {
    const contenedor = document.getElementById(contenedorId);

    // Datos generales del ejrcito
    const generales = [
        ["Ganador", ejer1.nombre],
        ["Golpe crtico", ejer1.golpesCriticos],
        ["Ataque efectivo", ejer1.ataqueEfectivo],
        ["Unidades ilesas", ejer1.unidadesIlesas],
        ["Unidades para ir al mdico o reparacin", ejer1.medico]
    ];

    // Unidades enemigas eliminadas
    const eliminadas = Object.entries(ejer2.unidadesPerdidas).map(
        ([tipo, cantidad]) => [`${tipo}`, cantidad]
    );

    // Unidades propias perdidas
    const perdidas = Object.entries(ejer1.unidadesPerdidas).map(
        ([tipo, cantidad]) => [`${tipo}`, cantidad]
    );

    // Crear y agregar las tablas
    contenedor.appendChild(crearTabla("Estadsticas Generales", generales));
    contenedor.appendChild(crearTabla("Unidades Enemigas Eliminadas", eliminadas));
    contenedor.appendChild(crearTabla("Unidades Propias Perdidas", perdidas));

    //Habilita el boton de nueva batalla
    const btnNuevaBatalla = document.getElementById("nueva-batalla");
    if (btnNuevaBatalla) {
        btnNuevaBatalla.disabled = false;
    }
}

//Crea las tablas de estadisticas
function crearTabla(tituloTexto, datos) {
    const divNuevo = document.createElement("div");
    divNuevo.style.border = "1";
    divNuevo.style.marginBottom = "20px";

    const titulo = document.createElement("h3");
    titulo.textContent = tituloTexto;
    divNuevo.appendChild(titulo);

    const tabla = document.createElement("table");
    tabla.border = "1";
    tabla.style.width = "100%";

    const tbody = document.createElement("tbody");

    //Seleccionar diferentes tablas y crear las tablas
    if (datos[0][0] === "Ganador") {
        datos.forEach(([descripcion, valor]) => {
            const fila = document.createElement("tr");
            fila.innerHTML = `<td>${descripcion}</td><td>${valor}</td>`;
            tbody.appendChild(fila);
        });
    } else {
        const thead = document.createElement("thead");
        const encabezado = document.createElement("tr");
        ["", "Unidad", "Cantidad"].forEach(texto => {
            const th = document.createElement("th");
            th.textContent = texto;
            encabezado.appendChild(th);
        });
        thead.appendChild(encabezado);
        tabla.appendChild(thead);

        datos.forEach(([nombre, cantidad]) => {
            const fila = document.createElement("tr");

            const celdaImg = document.createElement("td");
            const img = document.createElement("img");
            img.src = imagenes[nombre];
            img.alt = nombre;
            img.width = 30;
            img.height = 30;
            celdaImg.appendChild(img);

            // Nombre
            const celdaNombre = document.createElement("td");
            celdaNombre.textContent = nombre;

            // Cantidad
            const celdaCantidad = document.createElement("td");
            celdaCantidad.textContent = cantidad;

            fila.appendChild(celdaImg);
            fila.appendChild(celdaNombre);
            fila.appendChild(celdaCantidad);
            tbody.appendChild(fila);
        });

        tabla.appendChild(tbody);
        document.body.appendChild(tabla);
    }

    tabla.appendChild(tbody);
    divNuevo.appendChild(tabla);

    return divNuevo;
}

//Empieza la guerra con los dos ejercitos
async function iniciarGuerra() {
    let empieza = generateRandomNumber(1, 2);
    let gameover = false;
    let turno = 1;
    while (!gameover) {
        if (empieza === 1) {
            if (existeEjercitoEnemigo(ejercito1) && existeEjercitoEnemigo(ejercito2)) {
                atacarEjercito(ejercito1, ejercito2, resultados2, resultados1);
            }
            if (existeEjercitoEnemigo(ejercito2) && existeEjercitoEnemigo(ejercito1)) {
                atacarEjercito(ejercito2, ejercito1, resultados1, resultados2);
            } else {
                gameover = true;
            }
            mostrarTurno(ejercito1, "Ejrcito 1", turno);
            mostrarTurno(ejercito2, "Ejrcito 2", turno);
            actualizarEjercito(ejercito1, "Ejrcito 1", "ejercito1")
            actualizarEjercito(ejercito2, "Ejrcito 2", "ejercito2")
        } else {
            if (existeEjercitoEnemigo(ejercito2) && existeEjercitoEnemigo(ejercito1)) {
                atacarEjercito(ejercito2, ejercito1, resultados1, resultados2);
            }
            if (existeEjercitoEnemigo(ejercito2) && existeEjercitoEnemigo(ejercito1)) {
                atacarEjercito(ejercito1, ejercito2, resultados2, resultados1);
            } else {
                gameover = true;
            }
            mostrarTurno(ejercito2, "Ejrcito 2", turno);
            mostrarTurno(ejercito1, "Ejrcito 1", turno);
            actualizarEjercito(ejercito2, "Ejrcito 2", "ejercito2")
            actualizarEjercito(ejercito1, "Ejrcito 1", "ejercito1")
        }
        turno++;
        // Espera 2 segundos antes del siguiente turno
        await temporizador(2000);
    }
    if (existeEjercitoEnemigo(ejercito1)) {
        unidadesIlesas(ejercito1, resultados1)
        crearTablaEstadisticas(resultados1, resultados2, "logs")
    } else {
        unidadesIlesas(ejercito2, resultados2)
        crearTablaEstadisticas(resultados2, resultados1, "logs")
    }
}

